import axiosInstance from "../config/axiosConfig";

export const atualizarUser = async (
    username: string,
    status: string,
    sobre: string,
    email: string,
    senha: string,
    foto: File | null
) => {
    try {
        const responsePerfil = await axiosInstance.put("perfil/me", {
            Status: status,
            Sobre: sobre,
            FotoPerfil: foto
        })
        const responseUser = await axiosInstance.put("usuarios/me", {
            Username: username,
            Email: email,
            SenhaHash: senha,
        })

        return responseUser && responsePerfil.data
    } catch (error: any) {
        console.log("Erro:", error.responsePerfil?.data?.message || error.message)
        throw error
    }
}
