import { useState } from "react";
import styles from "./Login.module.scss";
import { Link } from "react-router-dom";
import { login } from "../../services/authService";
import axios from "axios";

const Login = async () => {

  const [formData, setFormData] = useState ({
    email:'',
    senha:'',
    message:'',
  })

  const userData = {
    email: 'adsflhj@gmail.com', // Exemplo de e-mail
    senha: '6845486',           // Exemplo de senha
  };

  try {
    const response = await axios.post('http://localhost:5218/api/auth/login', userData, {
      headers: {
        'Content-Type': 'application/json',
      },
    });
    console.log('Login bem-sucedido:', response.data);
  } catch (error) {
    console.error('Erro ao tentar fazer login:', error.response ? error.response.data : error.message);
  }


  /*
    const handleLogin = async (e: { preventDefault: () => void; }) => {
      e.preventDefault()

      console.log("Token armazenado no localStorage:", localStorage.getItem('token'));
      console.log(formData);
      try {
        const response = await login({email: formData.email, senha:formData.senha})
        localStorage.setItem("token", response.token)

        setFormData((prev) => ({ ... prev, message: 'login realizado com sucesso'}))

      } catch (error) {
        console.error("Erro ao tentar fazer login:", error);
        setFormData((prev) => ({...prev, message: 'Email ou senha invalidos'}))
      }

    }

    const handleChange = (e: {target: {name:any; value:any;}}) =>{
      const {name,value} = e.target
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }))
    }
  */
  return (
    <div className={styles.container}>
      <form  className={styles.form}>
        <h2><Link to="/"><a>Retorne</a></Link></h2>
        <div className={styles.labels}>
          <label htmlFor="" className={styles.label}>
            <h3>E-mail:</h3>
            <input 
              type='email'
              name='email'
              autoComplete='on'
              required
              placeholder='E-mail'

            />
          </label>
          <label htmlFor="" className={styles.label}>
            <h3>Senha:</h3>
            <input 
              type='password'
              name='senha'
              autoComplete='off'
              required
              placeholder='Senha'
            />
          </label>
        </div>   
        <div className={styles.footer}>
          <button type="submit">Entrar</button>
          <div className={styles.br}>
          </div>
          <p>ou crie sua conta <Link to='/CriarConta'><a>aqui</a></Link></p>
        </div>
      </form>
    </div>
  )
}

export default Login;
