import { useState } from 'react';
import styles from './EditPerfilModal.module.scss';  
type EditPerfilModalProps = {
    onClose: () => void; // Define que onClose é uma função sem retorno
  };

  const EditPerfilModal = () => {

  const [formData, setFormData] = useState({
    username: '',
    status: '',
    sobre: '',
    email: '',
    senha: '',
    foto: null,
  });

  const handleInputChange = (e: any) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e: any) => {
    setFormData({
      ...formData,
      foto: e.target.files[0],
    });
  };

  const handleSubmit = (e: any) => {
    e.preventDefault();
    // Aqui você pode enviar as informações para a API ou processá-las conforme necessário
    console.log(formData);
  };

  return (
    <div className={styles.modal}>
      <div className={styles.modalContent}>
        <button className={styles.closeButton} onClick={onClose}>X</button>
        <h2>Editar Perfil</h2>
        <form onSubmit={handleSubmit}>
          <div className={styles.formGroup}>
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleInputChange}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="status">Status</label>
            <input
              type="text"
              id="status"
              name="status"
              value={formData.status}
              onChange={handleInputChange}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="sobre">Sobre</label>
            <textarea
              id="sobre"
              name="sobre"
              value={formData.sobre}
              onChange={handleInputChange}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="email">E-mail</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="senha">Senha</label>
            <input
              type="password"
              id="senha"
              name="senha"
              value={formData.senha}
              onChange={handleInputChange}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="foto">Foto de Perfil</label>
            <input
              type="file"
              id="foto"
              name="foto"
              onChange={handleFileChange}
            />
          </div>
          <button type="submit" className={styles.submitButton}>Salvar</button>
        </form>
      </div>
    </div>
    )

}

export default EditPerfilModal
