import axiosInstance from "../config/axiosConfig";

export const atualizarUser = async (
    username: string,
    status: string,
    sobre: string,
    email: string,
    senha: string,
    foto: File | null
) => {
    const formData = new FormData();

    // Adicionando os dados do formulário
    formData.append("Username", username);
    formData.append("Email", email);
    formData.append("SenhaHash", senha);
    formData.append("Status", status);
    formData.append("Sobre", sobre);

    // Adicionando a foto, se existir
    if (foto) {
        formData.append("FotoPerfil", foto);
    }

    try {
        const responsePerfil = await axiosInstance.put("perfil/me", formData, {
          headers: {
            "Content-Type": "multipart/form-data", // Importante para enviar arquivos
          },
        });

        const responseUser = await axiosInstance.put("usuarios/me", formData, {
          headers: {
            "Content-Type": "multipart/form-data", // Importante para enviar arquivos
          },
        });

        return responseUser && responsePerfil.data    
    } catch (error: any) {
        console.log("Erro:", error.responsePerfil?.data?.message || error.message)
        throw error
    }
}
